import './App.css';
import {useState, useEffect} from 'react'
import Header from './components/Header';
import Product from './components/Product';
import Basket from './components/Basket';
import products from './products.json'

function App() {

	const [money, setMoney] = useState(128000000000)
	const [basket, setBasket] = useState([])
	const [total, setTotal] = useState(0)
	const health = useState(100)

	const resetBasket = () => {
		setBasket([])
	}

	//toplam fiyat hesaplaması
	useEffect(() => {
		setTotal(
			basket.reduce((acc, item) => {
				return acc + (item.amount * (products.find(product => product.id === item.id).price))
			}, 0),
		)
	}, [basket])

	return (
		<>
			<Header total={total} money={money} health={health}/>
			<div className="container products">
				{products.map(product => (
					<Product key={product.id} total={total} money={money} basket={basket} setBasket={setBasket} product={product} health={health}/>
				))}
			</div>
			{total > 0 && (
				<Basket resetBasket={resetBasket} products={products} total={total} basket={basket}/>
			)}
		</>
	);
}

export default App;
