import {useState} from 'react'
import {moneyFormat} from '../helpers';
import "bootstrap/dist/css/bootstrap.min.css";

function Header({total, money, health}) {


	return (
		<>

<div class="modal" tabindex="-1">
  <div class="modal-dialog">
    <div class="modal-content">
      <div class="modal-header">
        <h5 class="modal-title">Modal title</h5>
        <button type="button" class="btn-close" data-bs-dismiss="modal" aria-label="Close"></button>
      </div>
      <div class="modal-body">
        <p>Modal body text goes here.</p>
      </div>
      <div class="modal-footer">
        <button type="button" class="btn btn-secondary" data-bs-dismiss="modal">Close</button>
        <button type="button" class="btn btn-primary">Save changes</button>
      </div>
    </div>
  </div>
</div>
			{total > 0 && money - total !== 0 && (
				<div className="header">
          <span>$ {moneyFormat(money - total)}</span> Kaldı!
          <span><div Class="btn btn-primary" >akjsfka</div></span>
        </div>
			)}
			{total === 0 && (
				<div className="header"> 
        <span>$ {moneyFormat(money)}</span>
        Paranız Var
        <span><div Class="btn btn-primary">aksbfkjahsf</div></span>
        </div>
			)}
			{money - total === 0 && (
				<div className="header empty">Paran Bitti...
        <span><div Class="btn btn-primary">jkagsfkjgafj</div></span>
        </div>
			)}
			<style jsx>{`
              .header {
                position: sticky;
                top: 0;
                background: linear-gradient(to bottom, #20b820, #14be2a);
                height: 60px;
                display: flex;
                align-items: center;
                justify-content: center;
                color: #fff;
                font-size: 24px;
                letter-spacing: 1px;
              }

              .header.empty {
                background: linear-gradient(to bottom, #b82020, #be1414);
              }

              .header span {
                margin: 0 10px;
                font-weight: bold;
              }
			`}</style>
		</>
	)
}

export default Header
