import {useState} from 'react'
import {moneyFormat} from '../helpers';
import "bootstrap/dist/css/bootstrap.min.css";

function Header({total, money, health}) {


  const earnMoney = (prop) => {
    if(health >= 95){
      health -= 95;
      money += 1000;
    }
  }

	return (
		<>
			{total > 0 && money - total !== 0 && (
				<div className="header">
          <span>$ {moneyFormat(money - total)}</span> left!
          <span> Can {health}</span>
          <span><div Class="btn btn-primary" onClick={earnMoney} >Earn Money</div></span>
        </div>
			)}
			{total === 0 && (
				<div className="header">You have 
        <span>$ {moneyFormat(money)}</span>
        <span> Can {health}</span>
        <span><div Class="btn btn-primary" onClick={earnMoney}>Earn Money</div></span>
        </div>
			)}
			{money - total === 0 && (
				<div className="header empty">You're out of money... You meek!
        <span> Can {health}</span>
        <span><div Class="btn btn-primary" onClick={earnMoney}>Earn Money</div></span>
        </div>
			)}
			<style jsx>{`
              .header {
                position: sticky;
                top: 0;
                background: linear-gradient(to bottom, #20b820, #14be2a);
                height: 60px;
                display: flex;
                align-items: center;
                justify-content: center;
                color: #fff;
                font-size: 24px;
                letter-spacing: 1px;
              }

              .header.empty {
                background: linear-gradient(to bottom, #b82020, #be1414);
              }

              .header span {
                margin: 0 10px;
                font-weight: bold;
              }
			`}</style>
		</>
	)
}

export default Header
